b/SL4/Telerik.JustMock.Silverlight.xml"",""mode"":""100644"",""type"":""blob"",""sha"":""9a43128e518e9884aa9b5943f47b1c668de6cf32"",""size"":870081,""url"":""https://api.github.com/repos/erikdietrich/ChessTDD/git/blobs/9a43128e518e9884aa9b5943f47b1c668de6cf32""},{""path"":""packages/SpecFlow.1.9.0"",""mode"":""040000"",""type"":""tree"",""sha"":""425964dae87eb200aea84140139fc8dfcc6d4210"",""url"":""https://api.github.com/repos/erikdietrich/ChessTDD/git/trees/425964dae87eb200aea84140139fc8dfcc6d4210""},{""path"":""packages/SpecFlow.1.9.0/content"",""mode"":""040000"",""type"":""tree"",""sha"":""7b95b1c9a042b1125ce8d8acda3d977600090d50"",""url"":""https://api.github.com/repos/erikdietrich/ChessTDD/git/trees/7b95b1c9a042b1125ce8d8acda3d977600090d50""},{""path"":""packages/SpecFlow.1.9.0/content/App.config.transform"",""mode"":""100644"",""type"":""blob"",""sha"":""75b9125ecd40fdf13209e0c9d7d08bcb7834193c"",""size"":363,""url"":""https://api.github.com/repos/erikdietrich/ChessTDD/git/blobs/75b9125ecd40fdf13209e0c9d7d08bcb7834193c""},{""path"":""packages/repositories.config"",""mode"":""100644"",""type"":""blob"",""sha"":""0e7837037a2e3cf59c736f602055d358692f9693"",""size"":186,""url"":""https://api.github.com/repos/erikdietrich/ChessTDD/git/blobs/0e7837037a2e3cf59c736f602055d358692f9693""}],""truncated"":false}";
        private const string NotFoundTreeResult = @"{""message"":""Not Found"",""documentation_url"":""https://developer.github.com/v3""}";

        private IGithubInquisitor Inquisitor { get; set; }

        private ProfileSearcher Target { get; set; }

        [TestInitialize]
        public void BeforeEachTest()
        {
            Inquisitor = Mock.Create<IGithubInquisitor>();
            Inquisitor.Arrange(i => i.GetRepoSearchResults(Arg.AnyString)).Returns(SingleResult);
            Inquisitor.Arrange(i => i.GetRefForRepo(Arg.AnyString, Arg.AnyString)).Returns(RefResult);
            Inquisitor.Arrange(i => i.GetRecursiveTree(Arg.AnyString, Arg.AnyString, Arg.AnyString)).Returns(ChessTddRecursiveTreeResult);

            Target = new ProfileSearcher(Inquisitor);
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_No_Repositories_For_NotFound_UserId()
        {
            Inquisitor.Arrange(i => i.GetRepoSearchResults(Arg.AnyString)).Returns(NotFoundResult);

            var repos = Target.GetReposForUser("doesntmatter");

            Assert.IsFalse(repos.Any());
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_SingleRepository_For_Single_Result()
        {
            var repositoryCount = Target.GetReposForUser("whatever").Count();

            Assert.AreEqual<int>(1, repositoryCount);
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_A_Repository_With_Name_Matching_SingleResult_Name()
        {
            var firstRepo = Target.GetReposForUser("dontmatter").First();

            Assert.AreEqual<string>("ASPNETWebAPISamples", firstRepo.Name);
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_A_Repository_With_Url_Matching_SingleResult_Html_Url()
        {
            var firstRepo = Target.GetReposForUser("whatever").First();

            Assert.AreEqual<string>("https://github.com/erikdietrich/ASPNETWebAPISamples", firstRepo.Url);
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_A_Repository_With_Language_Matching_SingleResult_Language()
        {
            var firstRepo = Target.GetReposForUser("stilldoesn'tmatter").First();

            Assert.AreEqual<string>("JavaScript", firstRepo.Language);
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Throw_Exception_When_githubUserId_Is_Null()
        {
            ExtendedAssert.Throws<ArgumentException>(() => Target.GetReposForUser(null));
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_A_Repository_With_Sha_Value_Set()
        {
            var firstRepo = Target.GetReposForUser("erikdietrich").First();

            Assert.AreEqual<string>("1efc7000dfa02a86c2316546889b1224451e3413", firstRepo.Sha);
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_A_Null_sha_When_Ref_Result_Is_Not_Found()
        {
            Inquisitor.Arrange(i => i.GetRefForRepo(Arg.AnyString, Arg.AnyString)).Returns(NotfoundRefResult);

            var firstRepo = Target.GetReposForUser("erikdietrich").First();

            Assert.IsNull(firstRepo.Sha);
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Return_A_File_List_That_Includes_QueenMovesDotFeature()
        {
            var firstRepo = Target.GetReposForUser("erikdietrich").First();

            Assert.IsTrue(firstRepo.Files.Any(file => file == "Chess.Acceptance/QueenMoves.feature"));
        }

        [TestMethod, Owner("ebd"), TestCategory("Proven"), TestCategory("Unit")]
        public void Should_Return_Empty_FilesList_When_Tree_Result_Is_Not_Found()
        {
            Inquisitor.Arrange(i => i.GetRecursiveTree(Arg.AnyString, Arg.AnyString, Arg.AnyString)).Returns(NotFoundTreeResult);

            var firstRepo = Target.GetReposForUser("erikdietrich").First();

            Assert.AreEqual<int>(0, firstRepo.Files.Count());
        }
    }
